"use client";
import { useState } from "react";
// import Image from "next/image";
import "./page.css"
import * as React from 'react'


const fields = [
  { name: "age", label: "Age", type: "number" },
  { name: "weight", label: "Weight (kg)", type: "number" },
  { name: "height", label: "Height (cm)", type: "number" },
  // Add more fields here if needed later!
];

export default function Page() {

  const [ingredients, setIngredients] = useState("") //user input
  const [allergies, setAllergies] = useState<string[]>([])
  
  const handlecheckboxchange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { value, checked } = e.target;
    setAllergies(checked
      ? [...allergies, value]
      : allergies.filter((allergy) => allergy !== value));
    };

    const [formData, setFormData] = useState(
      fields.reduce((acc, field) => {
        acc[field.name] = "";
        return acc;
      }, {})
    );

    const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData((prev) => ({ ...prev, [name]: value }));
    };
  
    const handleSubmit = (e) => {
      e.preventDefault();
      console.log("Submitted:", formData);
    };
  

  return (
    <main className="min-h-screen bg-gradient-to-b from-orange-50 to-orange-100">
      <div className="bg-orange-500 h-auto w-full fixed top-0 z-50">

        <h1 className="text-6xl font-extrabold text-gray-900 tracking-wide uppercase mx-auto text-center">title</h1>
      </div>

        <div className="container mx-auto text-center py-12">
          <div className="flex flex-wrap justify-start items-center bg-white rounded-lg p-4 shadow-md mt-10 p-5 ">
          <form onSubmit={handleSubmit}>
            {fields.map((field) => (
              <div key={field.name} style={{ marginBottom: "1rem" }}>
                <label>
                  {field.label}:{" "}
                  <input
                    type={field.type}
                    name={field.name}
                    value={formData[field.name]}
                    onChange={handleChange}
                  />
                </label>
              </div>
            ))}
            <button type="submit">Submit</button>
          </form>

          </div>
          
          
        </div>
    </main>
  );
}
