"use client";
import { useState } from "react";
import "./page.css";
import * as React from "react";

const fields = [
  { name: "age", label: "Age", type: "number" },
  { name: "weight", label: "Weight (kg)", type: "number" },
  { name: "height", label: "Height (cm)", type: "number" },
];

const allergyOptions = ["Peanuts", "Dairy", "Gluten", "Seafood"];

export default function Page() {
  const [ingredients, setIngredients] = useState("");
  const [allergies, setAllergies] = useState<string[]>([]);

  const [formData, setFormData] = useState(
    fields.reduce((acc, field) => {
      acc[field.name] = "";
      return acc;
    }, {} as Record<string, string>)
  );

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleCheckboxChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { value, checked } = e.target;
    setAllergies((prev) =>
      checked ? [...prev, value] : prev.filter((a) => a !== value)
    );
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const submission = {
      ...formData,
      ingredients,
      allergies,
    };
    console.log("Submitted:", submission);
  };

  return (
    <main className="min-h-screen bg-gradient-to-b from-orange-50 to-orange-100">
      <div className="bg-orange-500 h-auto w-full fixed top-0 z-50">
        <h1 className="text-6xl font-extrabold text-gray-900 tracking-wide uppercase mx-auto text-center">
          title
        </h1>
      </div>

      <div className="container mx-auto text-center py-12">
        <div className="flex flex-wrap justify-start items-center bg-white rounded-lg p-4 shadow-md mt-10 p-5 ">
          <form onSubmit={handleSubmit}>
            {fields.map((field) => (
              <div key={field.name} className="mb-4">
                <label>
                  {field.label}:
                  <input
                    type={field.type}
                    name={field.name}
                    value={formData[field.name]}
                    onChange={handleChange}
                    className="ml-2 border rounded px-2"
                  />
                </label>
              </div>
            ))}

            <div className="mb-4">
              <label>
                Ingredients:
                <input
                  type="text"
                  value={ingredients}
                  onChange={(e) => setIngredients(e.target.value)}
                  className="ml-2 border rounded px-2"
                />
              </label>
            </div>

            <div className="mb-4">
              <p>Allergies:</p>
              {allergyOptions.map((item) => (
                <label key={item} className="mr-4">
                  <input
                    type="checkbox"
                    value={item}
                    checked={allergies.includes(item)}
                    onChange={handleCheckboxChange}
                    className="mr-1"
                  />
                  {item}
                </label>
              ))}
            </div>

            <button
              type="submit"
              className="bg-orange-500 text-white px-4 py-2 rounded"
            >
              Submit
            </button>
          </form>
        </div>
      </div>
    </main>
  );
}
